import {Map, Polygon,InfoWindow,Marker, HeatMap, GoogleApiWrapper} from 'google-maps-react';
import React from 'react'


export class MapContainer extends React.Component {
    fetchPlaces(mapProps, map) {
        const {google} = mapProps;
        const service = new google.maps.places.PlacesService(map);
        // ...
      }
    onMapClicked(mapProps, map, clickEvent) {
        // ...
        console.log(clickEvent.latLng.lat())
        console.log(clickEvent.latLng.lng())
      }

    render() {

          const gradient = [
            'rgba(0, 255, 255, 0)',
            'rgba(0, 255, 255, 1)',
            'rgba(0, 191, 255, 1)',
            'rgba(0, 127, 255, 1)',
            'rgba(0, 63, 255, 1)',
            'rgba(0, 0, 255, 1)',
            'rgba(0, 0, 223, 1)',
            'rgba(0, 0, 191, 1)',
            'rgba(0, 0, 159, 1)',
            'rgba(0, 0, 127, 1)',
            'rgba(63, 0, 91, 1)',
            'rgba(127, 0, 63, 1)',
            'rgba(191, 0, 31, 1)',
            'rgba(255, 0, 0, 1)'
          ];

          const positions = [
            { lat: 25.782551, lng: 85.445368, weight:4 },
            { lat: 25.792551, lng: 85.405368, weight:5 }

        ];
        return (
            <div>
            <Map
                style={{height: '100%', width: '80%', position: 'relative'}}
                className='map'
                google={this.props.google}
                zoom={4}
                onClick={this.onMapClicked}
                initialCenter={{
                    lat: 22.5937,
                    lng: 78.9629

                }}
            >

                <HeatMap
                    gradient={gradient}
                    opacity={0.6}
                    positions={positions}
                    radius={10}
                />

                </Map>

            </div>
        )
    }
}


export default GoogleApiWrapper({
       apiKey: "AIzaSyATgv28IzCv0bjkOBbAoejH-Z13BL4uV8U",
       libraries: ['visualization']
})(MapContainer)
